function display(){if(curPos<questions.length){document.getElementsByClassName("question")[0].innerHTML=questions[curPos].question;for(var e=0;4>e;e++)document.getElementsByTagName("button")[e].innerHTML=questions[curPos].choices[e]}else finish()}function unselect(){for(var e=0;e<document.getElementsByTagName("button").length-1;e++)document.getElementsByTagName("button")[e].style.background="rgb(110, 220, 200)"}function check(e){e.innerHTML==questions[curPos].choices[questions[curPos].correct-1]&&(console.log("correct"),score++)}function finish(){document.getElementsByClassName("container")[0].innerHTML="<div class='col-md-12 final'><h2>You have finished quiz</h2><h3>Your score is: "+score+"</h3></div>"}var curPos=0,selected=0,score=0;!function(){for(var e=0;4>e;e++)document.getElementsByTagName("button")[e].onclick=function(){unselect(),this.style.background="rgb(70, 180, 200)",selected=this}}(),document.getElementById("submit").onclick=function(){return selected?(check(selected),curPos++,display(),void unselect()):(alert("Please select an option!"),0)},display();